package javaLearning;

public class ArrayElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] arr= {5,6,3,2};
	
		for(int index=0;index<=3;index++)
		{		
			if(arr[index]%2!=0)
			{
			
			System.out.println("The odd number is:"+arr[index]);
		}
			
		
		
		
		}
	}

}
